import { useState, React } from "react"

function Somaliquotes(){
    const [index , setIndex] = useState(0)
    const [color , setcolor] = useState("rgb(39, 156, 156)")


    const xigmad = [
        "Aaddane eed ma waayo",
        "Iskaashato ma kufto",
        "Abeesadii lix jir kugu qaniintaa lixdan jir bay kugu dishaa"
    ]

    const colors = ["red","green","blue"]

    const next = () => {
        if(index < xigmad.length - 1){
            setIndex(index + 1)
           
        }
        let past = color
        for(let i = 1; i <= 3; i++){
            setcolor(colors[i])
        }
        past =             setcolor(colors[i])

    }

    const previous = () => {
            setIndex(index - 1)
            setcolor("red")
    }
    return <div>
        <p style={{color: color}}>{xigmad[index]}</p>
        <div className="btns">
            <button onClick={next}>Next</button>
            <button onClick={previous}>previous</button>
        </div>
    </div>
}

export default  Somaliquotes
